import React, { Component } from 'react';
import './App.css';

class Title extends Component {
// state goes here



// functions go here
flip = () => {
  document.getElementById('nameabout').innerHTML = "Hello I am hope myers";
  document.getElementsByClassName('title').style.fontSize = "1em";
  document.getElementsByClassName('title').style.fontFamily = "monospace";
}

flipback = () => {
  document.getElementById('nameabout').innerHTML = "Hope Myers"
}

  render() {
    return (
      <div className="title" id="nameabout" onMouseOver={this.flip} onMouseOut={this.flipback}>
      Hope Myers
      </div>
    );
  }
}
export default Title;
